import React from 'react'

import { BiSearch, BiChevronRight } from "react-icons/bi";


const NavSM = () => {

    return (
        <div className="text-gray">

            <div className="text-black">It's All Start Here</div>
            <span>

                bangaleru <BiChevronRight />

            </span>


        </div>
    );
};
//___________________________________________________

const NavMd = () => {

    return (
        <div className="w-full flex items-center gap-3 bg-white ">
            <BiSearch />
            <input type="search"
                className="w-full border-none focus:outline-none"
                placeholder="search for movie , events" />
        </div>

    );

};
const NavLg = () => { 

     return(
         
        <div className="">
        
           <div className="flex-">
           
           <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTeM6tePCCRuoDme2hfXJnFpAlU4KE3twK7fQ&usqp=CAU"    />
           
           
           
           
           </div>
          
        
        
        
        </div>
        
        );


};


//_________________________________


const Navbar = () => {
    return (
        <div className="bg-gray-100 p-4">

            <nav>

                <div className="md:hidden">

                    <NavSM />

                </div>   {/* mobile Screen*/}

                <div className="hidden md:flex lg:hidden"> 
                
                   <NavMd />
                </div>    {/* medium Screen*/}

                <div className="hidden lg:flex">  
                
                 <NavLg />
                
                
                </div>   {/* Large Screen*/}


            </nav>

        </div>
    );
};

export default Navbar
