import axios from 'axios';
import React, { useContext, useState, useEffect } from 'react'

import { useParams } from 'react-router-dom'

import Slider from 'react-slick';


import { FaCcVisa, FaCcApplePay } from 'react-icons/fa';

//component
import Cast from '../components/Cast/Cast.component';
import MovieHero from '../components/MovieHero/MovieHero.component';
import PosterSlider from '../components/PosterSlider/PosterSlider.component';

//config
import TempPosters from '../config/TempPosters.config';

//context
import { MovieContext } from '../context/Movie.context';



const Movie = () => {

    const { id } = useParams();
    const { movie } = useContext(MovieContext);
    const [cast, setCast] = useState([]);
    const [similerMovies,setSimilerMovies] = useState([]);
    const [recommended,set] = useState([]);


    useEffect(() => {

        const requestCast = async () => {

            const getCast = await axios.get(`/movie/${id}/credits`);

            setCast(getCast.data.cast);

        };


        requestCast();
    }, []);

    useEffect(() => {

        const requestGetPopularMovies = async () => {
    
          const getPopularMovies = await axios.get("/movie/popular");
    
          setPopularMovies(getPopularMovies.data.results);
    
    
        };
    
        requestGetPopularMovies();
    
      }, []);

      useEffect(() => {

        const requestGetPopularMovies = async () => {
    
          const getPopularMovies = await axios.get("/movie/popular");
    
          setPopularMovies(getPopularMovies.data.results);
    
    
        };
    
        requestGetPopularMovies();
    
      }, []);

    const settings = {

        infinite: false,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                },
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                },
            },
        ],
    };
    const settingsCast = {

        infinite: false,
        speed: 500,
        slidesToShow: 6,
        slidesToScroll: 4,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 3,
                    infinite: true,
                },
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 5,
                    slidesToScroll: 2,
                    initialSlide: 2,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <>

            <MovieHero />

            <div className=" my-12 container px-4 lg:px-20 lg:ml-5 lg:w-2/3">

                <div className="flex flex-col items-start gap-3">

                    <h2 className="text-gray-800 font-bold text-2xl">About The Movie</h2>

                    <p >{movie.overview}</p>

                </div>


                <div className="my-8">
                    <hr />
                </div>


                <div className="my-8">
                    <h2 className="text-gray-800 font-bold text-2xl my-3">Applicable Offer</h2>

                    <div className="flex flex-col gap-2 lg:flex-row">

                        <div className="flex items-start gap-2 bg-yellow-100 p-3 border-yellow-400 border-dashed border-2">

                            <div className="w-8 h-8">

                                <FaCcVisa className="w-full h-full" />

                            </div>


                            <div className="flex flex-col items-start">

                                <h3 className="text-gray-700 text-xl font-bold">Visa Stream Offer</h3>
                                <p className="text-gray-600"> Get 50% off up to INR 150 on all RuPay cards* on BookMyShow
                                    Stream.</p>

                            </div>

                        </div>

                        <div className="flex items-start gap-2 bg-yellow-100 p-3 border-yellow-400 border-dashed border-2">

                            <div className="w-8 h-8">

                                <FaCcApplePay className="w-full h-full" />

                            </div>


                            <div className="flex flex-col items-start">

                                <h3 className="text-gray-700 text-xl font-bold">Filmy Pass</h3>
                                <p className="text-gray-600">  Get Rs.75* off on 3 movies you buy/rent on Stream. Buy Filmy
                                    Pass @Rs.99</p>

                            </div>

                        </div>

                    </div>

                </div>

                <div className="my-8">
                    <hr />
                </div>
                <div className="my-8">
                    <h2 className="text-gray-800 font-bold text-2xl mb-4">Cast & Crew</h2>



                    <Slider {...settingsCast}>
                        {cast.map((castdata) => (

                            <Cast
                                image={`https://image.tmdb.org/t/p/original${castdata.profile_path}`}
                                castName={castdata.original_name}
                                role={castdata.character}
                            />

                        ))}

                    </Slider>



                </div>

                { /* ____________poster_______________ */}

                <div className="my-8">
                    <hr />
                </div>

                <div className="my-8">

                    <PosterSlider
                        config={settings}
                        images={TempPosters}
                        title="You Might Also Working"
                        isDark={false}
                    />

                </div>

                <div className="my-8">
                    <hr />
                </div>

                <div className="my-8">

                    <PosterSlider
                        config={settings}
                        images={TempPosters}
                        title="BMS Exclusive"
                        isDark={false}
                    />

                </div>

            </div>



        </>
    )
}

export default Movie;
