import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';





let count = 1;



const App = () => {



  const state = useState();


  const [count, setCount] = useState(0);



  const increment = () => {

    setCount( count+1 );

    console.log("clicked", count++);

  }

  return (

    <div class="App">

      <div className="App1 d-flex flex-row justify-content-center"> <h1>{count}</h1></div>

      <div className="App2 d-flex flex-row justify-content-center">

        <button className="btn btn-primary" onClick={increment}>Click me</button>

      </div>
    </div>
  )
}

export default App;
